import { useState } from "react";
import Rating from "./Rating";
import "./ReviewList.css";
import ReviewForm from "./ReviewForm";

function formatDate(value) {
  const data = new Date();
  return `${data.getFullYear()}. ${data.getMonth() + 1}. ${data.getDate()}`;
}

function ReviewListItem({ item, onDelete, onEdit }) {
  const handleDeleteClick = () => {
    onDelete(item.id);
  };

  const handleEditClick = () => {
    onEdit(item.id);
  };

  return (
    <div className="ReviewListItem">
      <img className="ReviewListItem-img" src={item.imgUrl} alt={item.title} />
      <div>
        <h1>{item.title}</h1>
        <Rating value={item.rating} />
        <p>{formatDate(item.createdAt)}</p>
        <p>{item.content}</p>
        <button onClick={handleDeleteClick}>삭제</button>
        {/* ReviewListItem 컴포넌트에서 수정 버튼이 클릭되면 onEdit 함수가 호출된다.
          이 함수는 수정할 리뷰의 ID를 ReviewList 컴포넌트로 전달한다.
        */}
        <button onClick={handleEditClick}>수정</button>
      </div>
    </div>
  );
}

function ReviewList({ items, onDelete }) {
  // 현재 수정중인 요소에 Id를 저장할 state
  // ReviewList 컴포넌트는 editingId 상태를 사용해 현재 수정 중인 리뷰의 ID를 추적한다.
  // 이 ID에 맞는 리뷰만 ReviewForm에서 수정할 수 있게 됩
  const [editingId, setEditingId] = useState(null);

  const handleCancel = () => setEditingId(null);
  return (
    <ul>
      {items.map((item) => {
        if (item.id === editingId) {
          const { imgUrl, title, rating, content } = item;
          const initialValues = { title, rating, content };
          return (
            <li key={item.id}>
              <ReviewForm
                initialValues={initialValues}
                initialPreview={imgUrl}
                onCancel={handleCancel}
              />
            </li>
          );
        }
        return (
          <li key={item.id}>
            <ReviewListItem
              item={item}
              onDelete={onDelete}
              onEdit={setEditingId}
            />
          </li>
        );
      })}
    </ul>
  );
}

export default ReviewList;
