import { useState } from "react";

function FileInput() {
  // 선택한 파일 객체를 담을 state
  const [value, setValue] = useState();

  const handleChange = (e) => {
    const nextValue = e.target.files[0];
    setValue(nextValue);
  };

  return <input type="file" onChange={handleChange} />;
}

export default FileInput;
